import React, { Component } from 'react';

import { Route, Link } from 'react-router-dom';
import styles from './Product.module.css';
import Button from '../../UI/Button/Button';
import ContactForm from '../../ContactForm/ContactForm';
import Backdrop from '../../UI/Backdrop/Backdrop';

export class Product extends Component {
  state = {
    openedPopup: false
  }

  orderBtnHandler = () => {
    console.log('qq')
    this.setState( (prevState) => {
      return { openedPopup: !prevState.openedPopup }
    });
  //   this.setState( ( prevState ) => {
  //     return { showSideDrawer: !prevState.showSideDrawer };
  // } );
  }

  render() {
    let product = null;
    let buttonText = 'Подробнее';
    let popup = null;
    if(this.props.priceTable) {
      buttonText = 'Заказать';
      product = (
        <table className={styles.table}>
            <tbody>
            <tr>
              <th>Обьем, м&sup3;</th>
              <th>Толшина, мм</th>
              <th>Цена Руб/м&sup3;</th>
            </tr>
              {this.props.priceTable.map((item, key) => {
                return (
                  <tr key={key}>
                    <td>{item.size}</td>
                    <td>{item.thickness}</td> 
                    <td>{item.price}</td>
                  </tr>
                )
              })}
            </tbody>
        </table>
      )
    };

    if(this.state.openedPopup) {
      popup = (
        <React.Fragment>
          <ContactForm name phone email message popup />
        </React.Fragment>
      )
    } else {
      popup = null;
    }
    console.log(this.props)
    return (
      <React.Fragment>
        <Backdrop show={this.state.openedPopup} clicked={this.orderBtnHandler} />
        <div className={styles.product}>
          <img src={this.props.url} alt='test' />
          <h3>{this.props.caption}</h3>
          <p>{this.props.desc}</p>
          {product}
          <Button type='button' clicked={this.orderBtnHandler}>{buttonText}</Button>
          <Link to={{
            pathname: '/catalog/' + this.props.caption,
            state: this.props.caption
          }}>zakaz</Link>
          {popup}
        </div>
        <Route path='/catalog/:id' exact component={ContactForm} />
      </React.Fragment>
    )
  }
}

export default Product
