import React from 'react';

import styles from './Product.module.css';
import Button from '../../UI/Button/Button';

function product(props) {
  let product = null;
  console.log(props.priceTable);
  if(props.priceTable) {

    product = (
    <table>
        <tbody>
          {props.priceTable.map(item => {
            return (
              <tr key={props.key}>
                <th>{item.size}</th>
                <th>{item.thickness}</th> 
                <th>{item.price}</th>
              </tr>
            )
          })}
        </tbody>
    </table>)

