import React, { Component } from 'react';

import { Link } from 'react-router-dom';

import styles from './Product.module.css';
import Button from '../../UI/Button/Button';
import ContactForm from '../../ContactForm/ContactForm';

export class Product extends Component {
  state = {
    openedPopup: false
  }

  render() {
    let product = null;
    let buttonText = 'Подробнее';
    let popup = null;
    if(this.props.priceTable) {
      buttonText = 'Заказать';
      product = (
        <table className={styles.table}>
            <tbody>
            <tr>
              <th>Обьем, м&sup3;</th>
              <th>Толшина, мм</th>
              <th>Цена Руб/м&sup3;</th>
            </tr>
              {this.props.priceTable.map((item, key) => {
                return (
                  <tr key={key}>
                    <td>{item.size}</td>
                    <td>{item.thickness}</td> 
                    <td>{item.price}</td>
                  </tr>
                )
              })}
            </tbody>
        </table>
      )
    };

    if(this.state.openedPopup) {
      popup = (
        <React.Fragment>
          <ContactForm name phone email message popup />
        </React.Fragment>
      )
    } else {
      popup = null;
    }
    console.log(this.props)
    return (
      <React.Fragment>
        <div className={styles.product}>
          <img src={this.props.url} alt='test' />
          <h3>{this.props.caption}</h3>
          <p>{this.props.desc}</p>
          {product}
          <Link className={styles.link} to={{
            pathname: '/catalog/' + this.props.caption,
            state: this.props.caption
          }}>
            Подробнее
          </Link>
          {popup}
        </div>

      </React.Fragment>
    )
  }
}

export default Product
